css:
      body {
        font-family:'Times New Roman', Times, serif;
        margin: 0;
        padding: 0;
        
        /*background: #f5f5f5;*/
      }
      .container {
        width: 100%;
        max-width: 800px;
        background: #fff;
        margin: 20px auto;
        padding: 20px;
        box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
      }
      .header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding-bottom: 10px;
      }
      .logo img {
        width: 150px;
      }
      .company-details {
        text-align: right;
        font-size: 14px;
      }
      .gst {
        color: red;
        font-weight: bold;
        font-size: 14px;
      }
      .quotation-details {
        margin: 20px 0;
      }
      .bold {
        font-weight: bold;
      }
      .subject {
        font-weight: bold;
        font-size: 16px;
      }
      .table {
        width: 100%;
        margin-top: 10px;
        border-collapse: collapse;
      }
      .table td,
      .table th {
        padding: 10px;
         border: 1px solid #ddd; 
      }
      .terms {
        margin-top: 40px;
      }
      .signature {
        margin-top: 40px;
      }
      .blue-link {
        color: blue;
        font-weight: bold;
        text-decoration: none;
      }
      .blue-link:hover {
        text-decoration: underline;
      }
      .signature img {
        width: 120px;
      }

      .ourbank {
        text-align: center;
        font-weight: 600;
        margin-top: 270px;
      }
      .ourbank span {
        color: red;
      }

      .instrument {
        text-align:center;
       
      }
      
      .instrument {
           margin-top: 20px;
      }
      .instrument img {
        width: 450px;
        height: 450px;
      }
   
      .instrument h3{
        text-decoration: underline;
      }
      ol li{
        font-weight: 600;
        text-align:left;
      }
      p{
        font-weight: 600;
      }
      .listofclient {
        max-width:900px;
        margin: 0 auto;
        padding: 20px;
        border-radius: 5px;
      }
      .listofclient h2 {
        text-align: center;
        font-weight: bold;
        text-decoration: underline;
      }
      .client-list {
        list-style: none;
        padding: 0;
      }
      .client-list li {
        display: flex;
        justify-content: flex-start;
        padding: 5px 10px;
      }
      .client-name,
      .client-location {
        flex:1;
        text-align: left;
        font-size:10px;
      }
.listofclient {
    page-break-before: always;
    page-break-inside: avoid;
    font-size: 13px; /* Slightly larger font for readability */
    line-height: 1.4; /* More spacing between lines */
    margin: 10px; /* Add margins for better layout */
}
.client-list {
    display: table;
    width: 100%;
}
.client-list li {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    padding: 4px 0; /* More breathing room */
}
.client-name {
    font-weight: bold;
    width: 65%; /* Allocate more space for names */
}
.client-location {
    text-align: left;
    width: 25%;
}
